        <p class="text-center">
          Die angeforderte Seite ist nicht verfügbar.<br>
        </p>
        <?php
          require_once "/var/www/php/website.inc.php";
          if (isset($_SERVER['REDIRECT_URL'])){
            // Eingabe überprüfen
            $lastSite = $_SERVER['REDIRECT_URL'];
            if($lastSite[0] != "/") $lastSite = "/" . $lastSite;
            if($lastSite[-1] != "/") $lastSite = $lastSite . "/";

            // Prepare Statement
            $stmt = $con->prepare("SELECT redirectAdress FROM 404link WHERE localAdress = ?");
            $stmt->bind_param('s', $lastSite);

            // Bei Fehler weiter mit 404
            if($stmt->execute()) {

              // Abfrageergebnisse speichern
              $result = $stmt->get_result();
              $stmt->fetch();

              // Testen ob Ergebnisse verfügbar sind
              if ($result->num_rows > 0) {
                foreach($result as $row) {
                  $redirectAdress = $row["redirectAdress"];
                  $stmt->close();
                  $con->close();
                  header("Location: $redirectAdress");
                  die();
                }
              }
            }
          }
          $stmt->close();
          $con->close();
        ?>
